import { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { UploadCloud, MessageCircle, Settings, FileText, Home, Sun, Moon, Monitor, Loader2, Plus, Trash2, Send, AlertCircle } from "lucide-react";

const API_BASE_URL = process.env.REACT_APP_API_URL || '/api';

export default function RagBotUI() {
  const [screen, setScreen] = useState("dashboard");
  const [docs, setDocs] = useState([]);
  const [bots, setBots] = useState([]);
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [sources, setSources] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [asking, setAsking] = useState(false);
  const [theme, setTheme] = useState("system");
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [error, setError] = useState("");
  const [backendStatus, setBackendStatus] = useState("checking"); // "checking", "connected", "disconnected"
  const [selectedBot, setSelectedBot] = useState(null);

  // Bot creation state
  const [showBotForm, setShowBotForm] = useState(false);
  const [newBot, setNewBot] = useState({
    name: "",
    instructions: "",
    model: "llama3",
    temperature: 0.7
  });

  // Settings state
  const [settings, setSettings] = useState({
    model: "llama3",
    temperature: 0.7,
    maxTokens: 2000,
    topP: 0.9
  });

  // Theme management
  useEffect(() => {
    const root = document.documentElement;
    const darkQuery = window.matchMedia("(prefers-color-scheme: dark)");

    const applyTheme = (t) => {
      root.classList.remove("light", "dark");
      root.classList.add(t === "system" ? (darkQuery.matches ? "dark" : "light") : t);
    };

    applyTheme(theme);
    const handleSystemChange = () => theme === "system" && applyTheme("system");
    darkQuery.addEventListener("change", handleSystemChange);
    return () => darkQuery.removeEventListener("change", handleSystemChange);
  }, [theme]);

  // Check backend health
  const checkBackendHealth = async () => {
    try {
      console.log("ðŸ” Checking backend health...");
      const response = await fetch(`${API_BASE_URL}/health`, {
        method: 'GET',
        headers: { 'Accept': 'application/json' },
      });

      if (response.ok) {
        setBackendStatus("connected");
        console.log("âœ… Backend is healthy");
        return true;
      } else {
        setBackendStatus("disconnected");
        console.log("âŒ Backend returned error:", response.status);
        return false;
      }
    } catch (err) {
      setBackendStatus("disconnected");
      console.error("âŒ Backend health check failed:", err);
      return false;
    }
  };

  // Fetch data on mount with retry
  useEffect(() => {
    const initializeApp = async () => {
      const isHealthy = await checkBackendHealth();

      if (isHealthy) {
        await fetchDocuments();
        await fetchBots();
      } else {
        setError("Cannot connect to backend. Make sure it's running on port 5000.");
      }
    };

    initializeApp();

    // Set up periodic health checks
    const healthCheckInterval = setInterval(checkBackendHealth, 30000); // Every 30 seconds

    return () => clearInterval(healthCheckInterval);
  }, []);

  const fetchDocuments = async () => {
    try {
      console.log("ðŸ” Fetching documents from:", `${API_BASE_URL}/documents`);

      const response = await fetch(`${API_BASE_URL}/documents`, {
        method: 'GET',
        headers: { 'Accept': 'application/json' },
      });

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const data = await response.json();
      console.log("âœ… Documents loaded:", data.documents?.length || 0);

      setDocs(data.documents || []);
      setError("");
    } catch (err) {
      console.error("âŒ Failed to fetch documents:", err);
      setDocs([]);
      // Don't show error if we're still initializing
      if (backendStatus === "connected") {
        setError(`Failed to load documents: ${err.message}`);
      }
    }
  };

  const fetchBots = async () => {
    try {
      console.log("ðŸ” Fetching bots from:", `${API_BASE_URL}/bots`);

      const response = await fetch(`${API_BASE_URL}/bots`, {
        method: 'GET',
        headers: { 'Accept': 'application/json' },
      });

      if (!response.ok) {
        throw new Error(`HTTP ${response.status}: ${response.statusText}`);
      }

      const data = await response.json();
      console.log("âœ… Bots loaded:", data.bots?.length || 0);

      setBots(data.bots || []);
    } catch (err) {
      console.error("âŒ Failed to fetch bots:", err);
      setBots([]);
      // Don't show error if we're still initializing
      if (backendStatus === "connected") {
        setError(`Failed to load bots: ${err.message}`);
      }
    }
  };

  // File upload handler
  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    setUploading(true);
    setError("");

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await fetch(`${API_BASE_URL}/upload`, {
        method: "POST",
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.error || `Upload failed with status ${response.status}`);
      }

      const result = await response.json();
      console.log("âœ… Upload successful:", result);

      await fetchDocuments();
      e.target.value = "";

      // Show success message
      alert(`âœ… ${result.message}\nChunks processed: ${result.chunks || 0}`);
    } catch (err) {
      console.error("Upload error:", err);
      setError(`Upload failed: ${err.message}`);
    } finally {
      setUploading(false);
    }
  };

  // Chat handler
  const handleAsk = async () => {
    if (!question.trim()) return;

    setAsking(true);
    setError("");
    setAnswer(""); // Clear previous answer
    setSources([]);

    try {
      const endpoint = selectedBot 
        ? `${API_BASE_URL}/bots/${selectedBot}/chat`
        : `${API_BASE_URL}/ask`;

      console.log("ðŸ’¬ Asking question to:", endpoint);

      const response = await fetch(endpoint, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ question }),
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.error || 'Failed to get answer');
      }

      const data = await response.json();
      console.log("âœ… Got answer:", data);

      setAnswer(data.answer);
      setSources(data.sources || []);
      setQuestion("");
    } catch (err) {
      console.error("Ask error:", err);
      setError(`Failed to get answer: ${err.message}`);
    } finally {
      setAsking(false);
    }
  };

  // Create bot
  const handleCreateBot = async () => {
    if (!newBot.name || !newBot.instructions) {
      alert("Please fill in all required fields");
      return;
    }

    try {
      const response = await fetch(`${API_BASE_URL}/bots/create`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newBot),
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({}));
        throw new Error(errorData.error || 'Failed to create bot');
      }

      const data = await response.json();
      console.log("âœ… Bot created:", data);

      await fetchBots();
      setShowBotForm(false);
      setNewBot({ name: "", instructions: "", model: "llama3", temperature: 0.7 });
      alert(`âœ… Bot "${data.bot.name}" created successfully!`);
    } catch (err) {
      alert(`Failed to create bot: ${err.message}`);
    }
  };

  // Delete bot
  const handleDeleteBot = async (botId) => {
    if (!window.confirm("Are you sure you want to delete this bot?")) return;

    try {
      const response = await fetch(`${API_BASE_URL}/bots/${botId}`, {
        method: 'DELETE',
      });

      if (!response.ok) throw new Error('Failed to delete bot');

      await fetchBots();
      if (selectedBot === botId) setSelectedBot(null);
      alert("âœ… Bot deleted successfully");
    } catch (err) {
      alert(`Failed to delete bot: ${err.message}`);
    }
  };

  const navItems = [
    { id: "dashboard", label: "Dashboard", icon: Home },
    { id: "uploads", label: "Uploads", icon: UploadCloud },
    { id: "chat", label: "Chat", icon: MessageCircle },
    { id: "bots", label: "Bots", icon: FileText },
    { id: "settings", label: "Settings", icon: Settings },
  ];

  return (
    <div className="min-h-screen bg-background text-foreground transition-colors duration-300">
      <div className="flex">
        {/* Sidebar */}
        <motion.aside
          initial={{ width: 240 }}
          animate={{ width: isSidebarOpen ? 240 : 60 }}
          className="bg-gray-900 p-4 flex flex-col border-r border-gray-700 min-h-screen"
        >
          <button onClick={() => setIsSidebarOpen(!isSidebarOpen)} className="text-white mb-4">
            {isSidebarOpen ? "â—„" : "â–º"}
          </button>
          <h2 className={`text-xl font-bold text-center mb-6 text-white ${!isSidebarOpen && "hidden"}`}>
            HKU RAG Bot
          </h2>

          {/* Backend Status Indicator */}
          {isSidebarOpen && (
            <div className={`mb-4 p-2 rounded text-xs flex items-center gap-2 ${
              backendStatus === "connected" ? "bg-green-900/30 text-green-400" :
              backendStatus === "disconnected" ? "bg-red-900/30 text-red-400" :
              "bg-yellow-900/30 text-yellow-400"
            }`}>
              <span className={`w-2 h-2 rounded-full ${
                backendStatus === "connected" ? "bg-green-400" :
                backendStatus === "disconnected" ? "bg-red-400" :
                "bg-yellow-400 animate-pulse"
              }`} />
              {backendStatus === "connected" && "Backend Connected"}
              {backendStatus === "disconnected" && "Backend Offline"}
              {backendStatus === "checking" && "Connecting..."}
            </div>
          )}

          <nav className="flex flex-col gap-2 flex-grow">
            {navItems.map((item) => (
              <button
                key={item.id}
                onClick={() => setScreen(item.id)}
                className={`flex items-center gap-2 p-2 rounded-lg text-left hover:bg-gray-700 transition ${
                  screen === item.id ? "bg-gray-700" : ""
                }`}
              >
                <item.icon className="w-5 h-5" />
                {isSidebarOpen && <span>{item.label}</span>}
              </button>
            ))}
          </nav>

          {/* HKU Login Placeholder */}
          {isSidebarOpen && (
            <div className="mt-auto pt-4 border-t border-gray-700">
              <div className="p-3 bg-gray-800 rounded-lg">
                <p className="text-xs text-gray-400 mb-2">HKU Account</p>
                <p className="text-sm text-gray-300">Login (Coming Soon)</p>
              </div>
            </div>
          )}
        </motion.aside>

        {/* Main Content */}
        <main className="flex-1 p-6 overflow-auto">
          {/* Backend Connection Error */}
          {backendStatus === "disconnected" && (
            <div className="mb-4 p-4 bg-red-900/50 border border-red-700 rounded-lg flex items-start gap-3">
              <AlertCircle className="w-5 h-5 text-red-400 flex-shrink-0 mt-0.5" />
              <div className="flex-1">
                <p className="text-red-200 font-semibold">Backend Connection Failed</p>
                <p className="text-red-300 text-sm mt-1">
                  Cannot connect to backend server. Please make sure it's running:
                </p>
                <code className="block bg-red-950/50 p-2 rounded mt-2 text-xs text-red-200">
                  cd backend/bot && source bin/activate && python app.py
                </code>
              </div>
              <button 
                onClick={checkBackendHealth}
                className="px-3 py-1 bg-red-700 hover:bg-red-600 text-white text-sm rounded transition"
              >
                Retry
              </button>
            </div>
          )}

          {/* General Error Messages */}
          {error && backendStatus === "connected" && (
            <div className="mb-4 p-4 bg-yellow-900/50 border border-yellow-700 rounded-lg flex justify-between items-center">
              <p className="text-yellow-200">{error}</p>
              <button onClick={() => setError("")} className="text-yellow-200 hover:text-white">âœ•</button>
            </div>
          )}

          <AnimatePresence mode="wait">
            {/* Dashboard */}
            {screen === "dashboard" && (
              <motion.div
                key="dashboard"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                className="space-y-6"
              >
                <h1 className="text-3xl font-bold">Dashboard</h1>
                <p className="text-gray-400">Your hub for document-based AI exploration with local, open-source models.</p>
                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <div className="bg-gray-800 p-4 rounded-lg shadow">
                    <h3 className="font-semibold text-gray-400">Documents</h3>
                    <p className="text-2xl font-bold">{docs.length}</p>
                  </div>
                  <div className="bg-gray-800 p-4 rounded-lg shadow">
                    <h3 className="font-semibold text-gray-400">Custom Bots</h3>
                    <p className="text-2xl font-bold">{bots.length}</p>
                  </div>
                  <div className="bg-gray-800 p-4 rounded-lg shadow">
                    <h3 className="font-semibold text-gray-400">LLM Model</h3>
                    <p className="text-2xl font-bold">{settings.model}</p>
                  </div>
                </div>

                <div className="bg-blue-900/20 border border-blue-700 p-4 rounded-lg">
                  <h3 className="font-semibold mb-2">ðŸš€ Agentic RAG System</h3>
                  <p className="text-sm text-gray-300">
                    This system uses LangGraph for intelligent document retrieval and reasoning.
                    All models run locally using Ollama - completely free and private!
                  </p>
                </div>

                {/* Backend Info */}
                <div className="bg-gray-800 p-4 rounded-lg">
                  <h3 className="font-semibold mb-2">ðŸ”— Backend Configuration</h3>
                  <div className="text-sm space-y-1">
                    <p className="text-gray-300">
                      API URL: <code className="bg-gray-900 px-2 py-1 rounded text-blue-400">{API_BASE_URL}</code>
                    </p>
                    <p className="text-gray-300">
                      Status: <span className={backendStatus === "connected" ? "text-green-400" : "text-red-400"}>
                        {backendStatus === "connected" ? "âœ“ Connected" : "âœ— Disconnected"}
                      </span>
                    </p>
                  </div>
                </div>
              </motion.div>
            )}

            {/* Uploads */}
            {screen === "uploads" && (
              <motion.div
                key="uploads"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                className="space-y-6 max-w-4xl"
              >
                <h1 className="text-3xl font-bold">Uploads</h1>
                <div className="border-2 border-dashed border-gray-600 p-6 rounded-lg text-center">
                  <UploadCloud className="w-12 h-12 mx-auto mb-2 text-gray-400" />
                  <p className="text-gray-400">Upload documents to build your RAG database</p>
                  <p className="text-sm text-gray-500 mt-1">(Text extraction API integration pending)</p>
                  <input
                    type="file"
                    onChange={handleFileUpload}
                    className="hidden"
                    id="file-upload"
                    accept=".txt,.pdf"
                    disabled={uploading}
                  />
                  <label
                    htmlFor="file-upload"
                    className={`mt-2 inline-flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded hover:bg-blue-700 transition ${
                      uploading ? "opacity-50 cursor-not-allowed" : "cursor-pointer"
                    }`}
                  >
                    {uploading && <Loader2 className="w-4 h-4 animate-spin" />}
                    {uploading ? "Uploading..." : "Choose File"}
                  </label>
                </div>

                {docs.length === 0 ? (
                  <div className="text-center text-gray-400 py-8">
                    No documents uploaded yet
                  </div>
                ) : (
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {docs.map((doc) => (
                      <div key={doc.id} className="bg-gray-800 p-4 rounded-lg shadow hover:bg-gray-750 transition">
                        <h3 className="font-semibold truncate">{doc.name}</h3>
                        <p className="text-sm text-gray-400">{doc.size}</p>
                        <p className="text-xs text-gray-500 mt-1">{new Date(doc.uploaded_at).toLocaleString()}</p>
                      </div>
                    ))}
                  </div>
                )}
              </motion.div>
            )}

            {/* Chat */}
            {screen === "chat" && (
              <motion.div
                key="chat"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                className="space-y-6 max-w-3xl"
              >
                <div className="flex justify-between items-center">
                  <h1 className="text-3xl font-bold">Chat with Documents</h1>
                  {bots.length > 0 && (
                    <select
                      value={selectedBot || ""}
                      onChange={(e) => setSelectedBot(e.target.value || null)}
                      className="px-3 py-2 bg-gray-800 border border-gray-700 rounded-lg"
                    >
                      <option value="">Default RAG Agent</option>
                      {bots.map((bot) => (
                        <option key={bot.id} value={bot.id}>{bot.name}</option>
                      ))}
                    </select>
                  )}
                </div>

                <div className="bg-gray-800 p-4 rounded-lg shadow space-y-4 min-h-[500px] flex flex-col">
                  <div className="flex-1 overflow-y-auto space-y-4">
                    {answer && (
                      <div className="space-y-4">
                        <div className="p-4 bg-blue-900/30 border border-blue-700 rounded-lg">
                          <p className="font-semibold mb-2 flex items-center gap-2">
                            <MessageCircle className="w-4 h-4" />
                            Answer:
                          </p>
                          <p className="whitespace-pre-wrap text-gray-200">{answer}</p>
                        </div>
                        {sources.length > 0 && (
                          <div className="p-4 bg-gray-700 rounded-lg">
                            <p className="font-semibold mb-2">ðŸ“š Sources:</p>
                            <div className="space-y-2">
                              {sources.map((source, idx) => (
                                <div key={idx} className="text-sm text-gray-300 p-3 bg-gray-800 rounded border border-gray-600">
                                  <p className="mb-1 text-gray-400 text-xs">
                                    {source.metadata.source}
                                  </p>
                                  <p>{source.content}</p>
                                </div>
                              ))}
                            </div>
                          </div>
                        )}
                      </div>
                    )}

                    {!answer && (
                      <div className="flex items-center justify-center h-full text-gray-400">
                        Ask a question to get started
                      </div>
                    )}
                  </div>

                  <div className="flex gap-2">
                    <input
                      type="text"
                      value={question}
                      onChange={(e) => setQuestion(e.target.value)}
                      onKeyPress={(e) => e.key === 'Enter' && handleAsk()}
                      placeholder="Ask a question about your documents..."
                      className="flex-1 p-3 bg-gray-900 border border-gray-700 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500"
                      disabled={asking}
                    />
                    <button
                      onClick={handleAsk}
                      disabled={asking || !question.trim()}
                      className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition disabled:opacity-50 disabled:cursor-not-allowed flex items-center gap-2"
                    >
                      {asking ? (
                        <>
                          <Loader2 className="w-4 h-4 animate-spin" />
                          Thinking...
                        </>
                      ) : (
                        <>
                          <Send className="w-4 h-4" />
                          Ask
                        </>
                      )}
                    </button>
                  </div>
                </div>
              </motion.div>
            )}

            {/* Bots */}
            {screen === "bots" && (
              <motion.div
                key="bots"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                className="space-y-6"
              >
                <div className="flex justify-between items-center">
                  <h1 className="text-3xl font-bold">Custom Bots</h1>
                  <button
                    onClick={() => setShowBotForm(!showBotForm)}
                    className="flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition"
                  >
                    <Plus className="w-4 h-4" />
                    Create New Bot
                  </button>
                </div>

                {showBotForm && (
                  <div className="bg-gray-800 p-6 rounded-lg shadow space-y-4">
                    <h2 className="text-xl font-semibold">New Bot Configuration</h2>
                    <input
                      type="text"
                      placeholder="Bot Name"
                      value={newBot.name}
                      onChange={(e) => setNewBot({...newBot, name: e.target.value})}
                      className="w-full p-3 bg-gray-900 border border-gray-700 rounded-lg"
                    />
                    <textarea
                      placeholder="Bot Instructions (e.g., 'You are a helpful assistant that summarizes documents...')"
                      value={newBot.instructions}
                      onChange={(e) => setNewBot({...newBot, instructions: e.target.value})}
                      className="w-full p-3 bg-gray-900 border border-gray-700 rounded-lg h-32"
                    />
                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <label className="block text-sm mb-2">Model</label>
                        <select
                          value={newBot.model}
                          onChange={(e) => setNewBot({...newBot, model: e.target.value})}
                          className="w-full p-3 bg-gray-900 border border-gray-700 rounded-lg"
                        >
                          <option value="llama3">Llama 3</option>
                          <option value="mistral">Mistral</option>
                          <option value="phi">Phi</option>
                        </select>
                      </div>
                      <div>
                        <label className="block text-sm mb-2">Temperature: {newBot.temperature}</label>
                        <input
                          type="range"
                          min="0"
                          max="1"
                          step="0.1"
                          value={newBot.temperature}
                          onChange={(e) => setNewBot({...newBot, temperature: parseFloat(e.target.value)})}
                          className="w-full"
                        />
                      </div>
                    </div>
                    <div className="flex gap-2">
                      <button
                        onClick={handleCreateBot}
                        className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition"
                      >
                        Create Bot
                      </button>
                      <button
                        onClick={() => setShowBotForm(false)}
                        className="px-4 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition"
                      >
                        Cancel
                      </button>
                    </div>
                  </div>
                )}

                {bots.length === 0 ? (
                  <div className="text-center text-gray-400 py-8">
                    No custom bots created yet
                  </div>
                ) : (
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {bots.map((bot) => (
                      <div key={bot.id} className="bg-gray-800 p-4 rounded-lg shadow hover:bg-gray-750 transition">
                        <div className="flex justify-between items-start mb-2">
                          <h3 className="font-semibold text-lg">{bot.name}</h3>
                          <button
                            onClick={() => handleDeleteBot(bot.id)}
                            className="text-red-400 hover:text-red-300"
                          >
                            <Trash2 className="w-4 h-4" />
                          </button>
                        </div>
                        <p className="text-sm text-gray-400 mb-2">{bot.instructions}</p>
                        <div className="flex gap-2 text-xs text-gray-500">
                          <span className="bg-gray-700 px-2 py-1 rounded">{bot.model}</span>
                          <span className="bg-gray-700 px-2 py-1 rounded">temp: {bot.temperature}</span>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </motion.div>
            )}

            {/* Settings */}
            {screen === "settings" && (
              <motion.div
                key="settings"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                className="space-y-6 max-w-2xl"
              >
                <h1 className="text-3xl font-bold">Settings</h1>

                {/* Theme Settings */}
                <div className="bg-gray-800 p-6 rounded-lg shadow space-y-4">
                  <h2 className="text-xl font-semibold">Appearance</h2>
                  <div>
                    <label className="block text-sm mb-2 text-gray-400">Theme</label>
                    <div className="flex gap-2">
                      <button
                        onClick={() => setTheme("light")}
                        className={`px-4 py-2 rounded-lg flex items-center gap-2 ${
                          theme === "light" ? "bg-blue-600 text-white" : "bg-gray-700 text-gray-300"
                        } hover:bg-blue-700 transition`}
                      >
                        <Sun className="w-4 h-4" /> Light
                      </button>
                      <button
                        onClick={() => setTheme("dark")}
                        className={`px-4 py-2 rounded-lg flex items-center gap-2 ${
                          theme === "dark" ? "bg-blue-600 text-white" : "bg-gray-700 text-gray-300"
                        } hover:bg-blue-700 transition`}
                      >
                        <Moon className="w-4 h-4" /> Dark
                      </button>
                      <button
                        onClick={() => setTheme("system")}
                        className={`px-4 py-2 rounded-lg flex items-center gap-2 ${
                          theme === "system" ? "bg-blue-600 text-white" : "bg-gray-700 text-gray-300"
                        } hover:bg-blue-700 transition`}
                      >
                        <Monitor className="w-4 h-4" /> System
                      </button>
                    </div>
                  </div>
                </div>

                {/* Model Settings */}
                <div className="bg-gray-800 p-6 rounded-lg shadow space-y-4">
                  <h2 className="text-xl font-semibold">Model Configuration</h2>

                  <div>
                    <label className="block text-sm mb-2 text-gray-400">Default Model</label>
                    <select
                      value={settings.model}
                      onChange={(e) => setSettings({...settings, model: e.target.value})}
                      className="w-full p-3 bg-gray-900 border border-gray-700 rounded-lg"
                    >
                      <option value="llama3">Llama 3 (Recommended)</option>
                      <option value="mistral">Mistral</option>
                      <option value="phi">Phi</option>
                    </select>
                  </div>

                  <div>
                    <label className="block text-sm mb-2 text-gray-400">
                      Temperature: {settings.temperature}
                    </label>
                    <input
                      type="range"
                      min="0"
                      max="1"
                      step="0.1"
                      value={settings.temperature}
                      onChange={(e) => setSettings({...settings, temperature: parseFloat(e.target.value)})}
                      className="w-full"
                    />
                    <p className="text-xs text-gray-500 mt-1">
                      Lower = more focused, Higher = more creative
                    </p>
                  </div>

                  <div>
                    <label className="block text-sm mb-2 text-gray-400">
                      Max Tokens: {settings.maxTokens}
                    </label>
                    <input
                      type="range"
                      min="500"
                      max="4000"
                      step="100"
                      value={settings.maxTokens}
                      onChange={(e) => setSettings({...settings, maxTokens: parseInt(e.target.value)})}
                      className="w-full"
                    />
                  </div>

                  <div>
                    <label className="block text-sm mb-2 text-gray-400">
                      Top P: {settings.topP}
                    </label>
                    <input
                      type="range"
                      min="0"
                      max="1"
                      step="0.05"
                      value={settings.topP}
                      onChange={(e) => setSettings({...settings, topP: parseFloat(e.target.value)})}
                      className="w-full"
                    />
                  </div>
                </div>

                {/* System Info */}
                <div className="bg-gray-800 p-6 rounded-lg shadow space-y-2">
                  <h2 className="text-xl font-semibold mb-4">System Information</h2>
                  <div className="space-y-2 text-sm">
                    <div className="flex justify-between">
                      <span className="text-gray-400">Backend Status:</span>
                      <span className="text-green-400">â— Connected</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-400">Vector Database:</span>
                      <span>ChromaDB</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-400">Embedding Model:</span>
                      <span>all-MiniLM-L6-v2</span>
                    </div>
                    <div className="flex justify-between">
                      <span className="text-gray-400">Agent Framework:</span>
                      <span>LangGraph</span>
                    </div>
                  </div>
                </div>
              </motion.div>
            )}
          </AnimatePresence>
        </main>
      </div>
    </div>
  );
}
