import { useState, useEffect, useRef } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { 
  UploadCloud, MessageCircle, Settings, FileText, Home, Loader2, 
  Plus, Trash2, Send, AlertCircle, RefreshCw, Check, X, ChevronRight 
} from "lucide-react";

const API_BASE_URL = '/api';

export default function RagBotUI() {
  const [screen, setScreen] = useState("dashboard");
  const [docs, setDocs] = useState([]);
  const [bots, setBots] = useState([]);
  const [chats, setChats] = useState([]);
  const [currentChat, setCurrentChat] = useState(null);
  const [question, setQuestion] = useState("");
  const [uploading, setUploading] = useState(false);
  const [asking, setAsking] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [error, setError] = useState("");
  const [backendStatus, setBackendStatus] = useState("checking");

  const [showBotForm, setShowBotForm] = useState(false);
  const [newBot, setNewBot] = useState({
    name: "",
    instructions: "",
    model: "llama3",
    temperature: 0.7
  });

  const [showChatConfig, setShowChatConfig] = useState(false);
  const [newChatConfig, setNewChatConfig] = useState({
    title: "",
    selected_documents: [],
    bot_id: null
  });

  const [settings, setSettings] = useState({
    model: "llama3",
    temperature: 0.7,
    maxTokens: 2000,
    topP: 0.9
  });

  const messagesEndRef = useRef(null);
  const eventSourceRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [currentChat?.messages]);

  const checkBackendHealth = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/health`);
      if (response.ok) {
        const data = await response.json();
        setBackendStatus("connected");
        if (!data.ollama_available) {
          setError("âš ï¸ Ollama is not running. Start it with: ollama serve");
        }
        return true;
      } else {
        setBackendStatus("disconnected");
        return false;
      }
    } catch (err) {
      setBackendStatus("disconnected");
      return false;
    }
  };

  useEffect(() => {
    const init = async () => {
      const healthy = await checkBackendHealth();
      if (healthy) {
        await fetchDocuments();
        await fetchBots();
        await fetchChats();
      }
    };
    init();
    const interval = setInterval(checkBackendHealth, 30000);
    return () => clearInterval(interval);
  }, []);

  const fetchDocuments = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/documents`);
      if (response.ok) {
        const data = await response.json();
        setDocs(data.documents || []);
        setError("");
      }
    } catch (err) {
      console.error("Failed to fetch documents:", err);
    }
  };

  const fetchBots = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/bots`);
      if (response.ok) {
        const data = await response.json();
        setBots(data.bots || []);
      }
    } catch (err) {
      console.error("Failed to fetch bots:", err);
    }
  };

  const fetchChats = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/chats`);
      if (response.ok) {
        const data = await response.json();
        setChats(data.chats || []);
      }
    } catch (err) {
      console.error("Failed to fetch chats:", err);
    }
  };

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    setUploading(true);
    setError("");

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await fetch(`${API_BASE_URL}/upload`, {
        method: "POST",
        body: formData,
      });

      const result = await response.json();

      if (!response.ok) {
        throw new Error(result.error || 'Upload failed');
      }

      await fetchDocuments();
      e.target.value = "";

      const message = result.type === "image" 
        ? `âœ… ${result.message}\n\nâš ï¸ ${result.note}`
        : `âœ… ${result.message}\nðŸ“Š Chunks processed: ${result.chunks}`;

      alert(message);
    } catch (err) {
      setError(`Upload failed: ${err.message}`);
    } finally {
      setUploading(false);
    }
  };

  const handleDeleteDocument = async (docId) => {
    if (!window.confirm("Are you sure you want to delete this document?")) return;

    try {
      const response = await fetch(`${API_BASE_URL}/documents/${docId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        await fetchDocuments();
        alert("âœ… Document deleted successfully");
      } else {
        throw new Error('Failed to delete document');
      }
    } catch (err) {
      alert(`Failed to delete document: ${err.message}`);
    }
  };

  const handleCreateChat = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/chats/create`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          title: newChatConfig.title || "New Chat",
          selected_documents: newChatConfig.selected_documents,
          bot_id: newChatConfig.bot_id
        }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to create chat');
      }

      await fetchChats();
      setCurrentChat(data.chat);
      setShowChatConfig(false);
      setNewChatConfig({ title: "", selected_documents: [], bot_id: null });
      setScreen("chat");
    } catch (err) {
      alert(`Failed to create chat: ${err.message}`);
    }
  };

  const handleDeleteChat = async (chatId) => {
    if (!window.confirm("Delete this chat?")) return;

    try {
      const response = await fetch(`${API_BASE_URL}/chats/${chatId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        await fetchChats();
        if (currentChat?.id === chatId) {
          setCurrentChat(null);
        }
      }
    } catch (err) {
      alert(`Failed to delete chat: ${err.message}`);
    }
  };

  const handleSelectChat = async (chatId) => {
    try {
      const response = await fetch(`${API_BASE_URL}/chats/${chatId}`);
      if (response.ok) {
        const data = await response.json();
        setCurrentChat(data.chat);
        setScreen("chat");
      }
    } catch (err) {
      console.error("Failed to load chat:", err);
    }
  };

  const handleAskStreaming = async () => {
    if (!question.trim() || !currentChat) return;

    setAsking(true);
    setError("");

    const userMessage = {
      id: Date.now().toString(),
      role: "user",
      content: question,
      timestamp: new Date().toISOString()
    };

    const assistantMessage = {
      id: (Date.now() + 1).toString(),
      role: "assistant",
      content: "",
      sources: [],
      timestamp: new Date().toISOString(),
      streaming: true
    };

    setCurrentChat(prev => ({
      ...prev,
      messages: [...prev.messages, userMessage, assistantMessage]
    }));

    const currentQuestion = question;
    setQuestion("");

    try {
      const response = await fetch(`${API_BASE_URL}/chats/${currentChat.id}/ask`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ question: currentQuestion }),
      });

      if (!response.ok) {
        throw new Error('Failed to get response');
      }

      const reader = response.body.getReader();
      const decoder = new TextDecoder();
      let buffer = '';

      while (true) {
        const { done, value } = await reader.read();
        if (done) break;

        buffer += decoder.decode(value, { stream: true });
        const lines = buffer.split('\n');
        buffer = lines.pop() || '';

        for (const line of lines) {
          if (!line.trim() || !line.startsWith('data: ')) continue;

          try {
            const data = JSON.parse(line.slice(6));

            if (data.token) {
              setCurrentChat(prev => {
                const messages = [...prev.messages];
                const lastMsg = messages[messages.length - 1];
                if (lastMsg && lastMsg.role === 'assistant' && lastMsg.streaming) {
                  lastMsg.content += data.token;
                }
                return { ...prev, messages };
              });
            }

            if (data.sources) {
              setCurrentChat(prev => {
                const messages = [...prev.messages];
                const lastMsg = messages[messages.length - 1];
                if (lastMsg && lastMsg.role === 'assistant') {
                  lastMsg.sources = data.sources;
                }
                return { ...prev, messages };
              });
            }

            if (data.done) {
              setCurrentChat(prev => {
                const messages = [...prev.messages];
                const lastMsg = messages[messages.length - 1];
                if (lastMsg && lastMsg.role === 'assistant') {
                  delete lastMsg.streaming;
                }
                return { ...prev, messages };
              });
            }

            if (data.error) {
              throw new Error(data.error);
            }
          } catch (parseError) {
            // Ignore parse errors silently
          }
        }
      }

      await fetchChats();

    } catch (err) {
      setError(`Failed to get answer: ${err.message}`);
      setCurrentChat(prev => {
        const messages = prev.messages.slice(0, -1);
        return { ...prev, messages };
      });
    } finally {
      setAsking(false);
    }
  };

  const handleCreateBot = async () => {
    if (!newBot.name || !newBot.instructions) {
      alert("Please fill in all required fields");
      return;
    }

    try {
      const response = await fetch(`${API_BASE_URL}/bots/create`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newBot),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to create bot');
      }

      await fetchBots();
      setShowBotForm(false);
      setNewBot({ name: "", instructions: "", model: "llama3", temperature: 0.7 });
      alert(`âœ… Bot "${data.bot.name}" created successfully!`);
    } catch (err) {
      alert(`Failed to create bot: ${err.message}`);
    }
  };

  const handleDeleteBot = async (botId) => {
    if (!window.confirm("Are you sure you want to delete this bot?")) return;

    try {
      const response = await fetch(`${API_BASE_URL}/bots/${botId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        await fetchBots();
        alert("âœ… Bot deleted successfully");
      } else {
        throw new Error('Failed to delete bot');
      }
    } catch (err) {
      alert(`Failed to delete bot: ${err.message}`);
    }
  };

  const toggleDocumentSelection = (docId) => {
    setNewChatConfig(prev => {
      const selected = prev.selected_documents.includes(docId)
        ? prev.selected_documents.filter(id => id !== docId)
        : [...prev.selected_documents, docId];
      return { ...prev, selected_documents: selected };
    });
  };

  const handleCleanupDuplicates = async () => {
    if (!window.confirm("Remove duplicate chunks from vector database?")) return;

    try {
      const response = await fetch(`${API_BASE_URL}/documents/cleanup`, {
        method: 'POST',
      });

      const result = await response.json();

      if (response.ok) {
        alert(`âœ… ${result.message}`);
        await fetchDocuments();
      } else {
        alert(`âŒ Cleanup failed: ${result.error}`);
      }
    } catch (err) {
      alert(`Failed to cleanup: ${err.message}`);
    }
  };

  const navItems = [
    { id: "dashboard", label: "Dashboard", icon: Home },
    { id: "uploads", label: "Uploads", icon: UploadCloud },
    { id: "chat", label: "Chat", icon: MessageCircle },
    { id: "bots", label: "Bots", icon: FileText },
    { id: "settings", label: "Settings", icon: Settings },
  ];

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      <div className="flex">
        <motion.aside
          initial={{ width: 240 }}
          animate={{ width: isSidebarOpen ? 240 : 60 }}
          className="bg-gray-800 p-4 flex flex-col border-r border-gray-700 min-h-screen"
        >
          <button onClick={() => setIsSidebarOpen(!isSidebarOpen)} className="text-white mb-4 hover:text-blue-400">
            {isSidebarOpen ? "â—„" : "â–º"}
          </button>
          <h2 className={`text-xl font-bold text-center mb-6 ${!isSidebarOpen && "hidden"}`}>
            HKU RAG Bot
          </h2>

          {isSidebarOpen && (
            <div className={`mb-4 p-2 rounded text-xs flex items-center gap-2 ${
              backendStatus === "connected" ? "bg-green-900/30 text-green-400" :
              backendStatus === "disconnected" ? "bg-red-900/30 text-red-400" :
              "bg-yellow-900/30 text-yellow-400"
            }`}>
              <span className={`w-2 h-2 rounded-full ${
                backendStatus === "connected" ? "bg-green-400" :
                backendStatus === "disconnected" ? "bg-red-400" :
                "bg-yellow-400 animate-pulse"
              }`} />
              {backendStatus === "connected" && "Connected"}
              {backendStatus === "disconnected" && "Offline"}
              {backendStatus === "checking" && "Connecting..."}
            </div>
          )}

          <nav className="flex flex-col gap-2 flex-grow">
            {navItems.map((item) => (
              <button
                key={item.id}
                onClick={() => setScreen(item.id)}
                className={`flex items-center gap-2 p-2 rounded-lg text-left hover:bg-gray-700 transition ${
                  screen === item.id ? "bg-gray-700 text-blue-400" : ""
                }`}
              >
                <item.icon className="w-5 h-5" />
                {isSidebarOpen && <span>{item.label}</span>}
              </button>
            ))}
          </nav>

          {isSidebarOpen && (
            <div className="mt-auto pt-4 border-t border-gray-700">
              <div className="p-3 bg-gray-700 rounded-lg">
                <p className="text-xs text-gray-400 mb-2">HKU Account</p>
                <p className="text-sm">Login (Coming Soon)</p>
              </div>
            </div>
          )}
        </motion.aside>

        {screen === "chat" && (
          <motion.aside
            initial={{ width: 250 }}
            className="bg-gray-800 border-r border-gray-700 flex flex-col w-64"
          >
            <div className="p-4 border-b border-gray-700">
              <button
                onClick={() => setShowChatConfig(true)}
                className="w-full flex items-center justify-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition"
              >
                <Plus className="w-4 h-4" />
                New Chat
              </button>
            </div>

            <div className="flex-1 overflow-y-auto p-2">
              {chats.length === 0 ? (
                <div className="text-center text-gray-400 py-8 text-sm">
                  <MessageCircle className="w-8 h-8 mx-auto mb-2 opacity-50" />
                  <p>No chats yet</p>
                </div>
