import { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { UploadCloud, MessageCircle, Settings, FileText, Home, Loader2, Plus, Trash2, Send, AlertCircle, RefreshCw } from "lucide-react";

const API_BASE_URL = '/api';

export default function RagBotUI() {
  const [screen, setScreen] = useState("dashboard");
  const [docs, setDocs] = useState([]);
  const [bots, setBots] = useState([]);
  const [question, setQuestion] = useState("");
  const [answer, setAnswer] = useState("");
  const [sources, setSources] = useState([]);
  const [uploading, setUploading] = useState(false);
  const [asking, setAsking] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [error, setError] = useState("");
  const [backendStatus, setBackendStatus] = useState("checking");
  const [selectedBot, setSelectedBot] = useState(null);

  const [showBotForm, setShowBotForm] = useState(false);
  const [newBot, setNewBot] = useState({
    name: "",
    instructions: "",
    model: "llama3",
    temperature: 0.7
  });

  const [settings, setSettings] = useState({
    model: "llama3",
    temperature: 0.7,
    maxTokens: 2000,
    topP: 0.9
  });

  const checkBackendHealth = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/health`);
      if (response.ok) {
        const data = await response.json();
        setBackendStatus("connected");
        if (!data.ollama_available) {
          setError("âš ï¸ Ollama is not running. Start it with: ollama serve");
        }
        return true;
      } else {
        setBackendStatus("disconnected");
        return false;
      }
    } catch (err) {
      setBackendStatus("disconnected");
      return false;
    }
  };

  useEffect(() => {
    const init = async () => {
      const healthy = await checkBackendHealth();
      if (healthy) {
        await fetchDocuments();
        await fetchBots();
      }
    };
    init();
    const interval = setInterval(checkBackendHealth, 30000);
    return () => clearInterval(interval);
  }, []);

  const fetchDocuments = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/documents`);
      if (response.ok) {
        const data = await response.json();
        setDocs(data.documents || []);
        setError("");
      }
    } catch (err) {
      console.error("Failed to fetch documents:", err);
    }
  };

  const fetchBots = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/bots`);
      if (response.ok) {
        const data = await response.json();
        setBots(data.bots || []);
      }
    } catch (err) {
      console.error("Failed to fetch bots:", err);
    }
  };

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    setUploading(true);
    setError("");

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await fetch(`${API_BASE_URL}/upload`, {
        method: "POST",
        body: formData,
      });

      const result = await response.json();

      if (!response.ok) {
        throw new Error(result.error || 'Upload failed');
      }

      await fetchDocuments();
      e.target.value = "";

      const message = result.type === "image" 
        ? `âœ… ${result.message}\n\nâš ï¸ ${result.note}`
        : `âœ… ${result.message}\nðŸ“Š Chunks processed: ${result.chunks}`;

      alert(message);
    } catch (err) {
      setError(`Upload failed: ${err.message}`);
    } finally {
      setUploading(false);
    }
  };

  const handleDeleteDocument = async (docId) => {
    if (!window.confirm("Are you sure you want to delete this document?")) return;

    try {
      const response = await fetch(`${API_BASE_URL}/documents/${docId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        await fetchDocuments();
        alert("âœ… Document deleted successfully");
      } else {
        throw new Error('Failed to delete document');
      }
    } catch (err) {
      alert(`Failed to delete document: ${err.message}`);
    }
  };

  const handleAsk = async () => {
    if (!question.trim()) return;

    setAsking(true);
    setError("");
    setAnswer("");
    setSources([]);

    try {
      const endpoint = selectedBot 
        ? `${API_BASE_URL}/bots/${selectedBot}/chat`
        : `${API_BASE_URL}/ask`;

      const response = await fetch(endpoint, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ question }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to get answer');
      }

      setAnswer(data.answer);
      setSources(data.sources || []);
      setQuestion("");
    } catch (err) {
      setError(`Failed to get answer: ${err.message}`);
    } finally {
      setAsking(false);
    }
  };

  const handleCreateBot = async () => {
    if (!newBot.name || !newBot.instructions) {
      alert("Please fill in all required fields");
      return;
    }

    try {
      const response = await fetch(`${API_BASE_URL}/bots/create`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newBot),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to create bot');
      }

      await fetchBots();
      setShowBotForm(false);
      setNewBot({ name: "", instructions: "", model: "llama3", temperature: 0.7 });
      alert(`âœ… Bot "${data.bot.name}" created successfully!`);
    } catch (err) {
      alert(`Failed to create bot: ${err.message}`);
    }
  };

  const handleDeleteBot = async (botId) => {
    if (!window.confirm("Are you sure you want to delete this bot?")) return;

    try {
      const response = await fetch(`${API_BASE_URL}/bots/${botId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        await fetchBots();
        if (selectedBot === botId) setSelectedBot(null);
        alert("âœ… Bot deleted successfully");
      } else {
        throw new Error('Failed to delete bot');
      }
    } catch (err) {
      alert(`Failed to delete bot: ${err.message}`);
    }
  };

  const navItems = [
    { id: "dashboard", label: "Dashboard", icon: Home },
    { id: "uploads", label: "Uploads", icon: UploadCloud },
    { id: "chat", label: "Chat", icon: MessageCircle },
    { id: "bots", label: "Bots", icon: FileText },
    { id: "settings", label: "Settings", icon: Settings },
  ];

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      <div className="flex">
        <motion.aside
          initial={{ width: 240 }}
          animate={{ width: isSidebarOpen ? 240 : 60 }}
          className="bg-gray-800 p-4 flex flex-col border-r border-gray-700 min-h-screen"
        >
          <button onClick={() => setIsSidebarOpen(!isSidebarOpen)} className="text-white mb-4 hover:text-blue-400">
            {isSidebarOpen ? "â—„" : "â–º"}
          </button>
          <h2 className={`text-xl font-bold text-center mb-6 ${!isSidebarOpen && "hidden"}`}>
            HKU RAG Bot
          </h2>

          {isSidebarOpen && (
            <div className={`mb-4 p-2 rounded text-xs flex items-center gap-2 ${
              backendStatus === "connected" ? "bg-green-900/30 text-green-400" :
              backendStatus === "disconnected" ? "bg-red-900/30 text-red-400" :
              "bg-yellow-900/30 text-yellow-400"
            }`}>
              <span className={`w-2 h-2 rounded-full ${
                backendStatus === "connected" ? "bg-green-400" :
                backendStatus === "disconnected" ? "bg-red-400" :
                "bg-yellow-400 animate-pulse"
              }`} />
              {backendStatus === "connected" && "Connected"}
              {backendStatus === "disconnected" && "Offline"}
              {backendStatus === "checking" && "Connecting..."}
            </div>
          )}

          <nav className="flex flex-col gap-2 flex-grow">
            {navItems.map((item) => (
              <button
                key={item.id}
                onClick={() => setScreen(item.id)}
                className={`flex items-center gap-2 p-2 rounded-lg text-left hover:bg-gray-700 transition ${
                  screen === item.id ? "bg-gray-700 text-blue-400" : ""
                }`}
              >
                <item.icon className="w-5 h-5" />
                {isSidebarOpen && <span>{item.label}</span>}
              </button>
            ))}
          </nav>

          {isSidebarOpen && (
            <div className="mt-auto pt-4 border-t border-gray-700">
              <div className="p-3 bg-gray-700 rounded-lg">
                <p className="text-xs text-gray-400 mb-2">HKU Account</p>
                <p className="text-sm">Login (Coming Soon)</p>
              </div>
            </div>
          )}
        </motion.aside>

        <main className="flex-1 p-6 overflow-auto bg-gray-900">
          {backendStatus === "disconnected" && (
            <div className="mb-4 p-4 bg-red-900/50 border border-red-700 rounded-lg flex items-start gap-3">
              <AlertCircle className="w-5 h-5 text-red-400 flex-shrink-0 mt-0.5" />
              <div className="flex-1">
                <p className="text-red-200 font-semibold">Backend Connection Failed</p>
                <p className="text-red-300 text-sm mt-1">
                  Start backend: cd backend/bot && source bin/activate && python app.py
                </p>
              </div>
              <button 
                onClick={checkBackendHealth}
                className="px-3 py-1 bg-red-700 hover:bg-red-600 text-white text-sm rounded transition flex items-center gap-1"
              >
                <RefreshCw className="w-3 h-3" />
                Retry
              </button>
            </div>
          )}

          {error && backendStatus === "connected" && (
            <div className="mb-4 p-4 bg-yellow-900/50 border border-yellow-700 rounded-lg flex justify-between items-center">
              <p className="text-yellow-200">{error}</p>
              <button onClick={() => setError("")} className="text-yellow-200 hover:text-white">âœ•</button>
            </div>
          )}

          <AnimatePresence mode="wait">
            {screen === "dashboard" && (
              <motion.div
                key="dashboard"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                className="space-y-6"
              >
                <h1 className="text-3xl font-bold">Dashboard</h1>
                <p className="text-gray-400">Document-based AI with local, open-source models</p>

                <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                  <div className="bg-gray-800 p-6 rounded-lg shadow border border-gray-700">
                    <h3 className="font-semibold text-gray-400 mb-2">Documents</h3>
                    <p className="text-3xl font-bold text-blue-400">{docs.length}</p>
                  </div>
                  <div className="bg-gray-800 p-6 rounded-lg shadow border border-gray-700">
                    <h3 className="font-semibold text-gray-400 mb-2">Custom Bots</h3>
                    <p className="text-3xl font-bold text-green-400">{bots.length}</p>
                  </div>
                  <div className="bg-gray-800 p-6 rounded-lg shadow border border-gray-700">
                    <h3 className="font-semibold text-gray-400 mb-2">LLM Model</h3>
                    <p className="text-2xl font-bold text-purple-400">{settings.model}</p>
                  </div>
                </div>

                <div className="bg-blue-900/20 border border-blue-700 p-4 rounded-lg">
                  <h3 className="font-semibold mb-2">ðŸš€ Agentic RAG System</h3>
                  <p className="text-sm text-gray-300">
                    RAG system with LangChain. All models run locally using Ollama - completely free and private!
                  </p>
                </div>

                <div className="bg-gray-800 border border-gray-700 p-4 rounded-lg">
                  <h3 className="font-semibold mb-3">ðŸ“‹ Supported Formats</h3>
                  <div className="grid grid-cols-2 md:grid-cols-3 gap-2 text-sm">
                    <div className="flex items-center gap-2 text-green-400">
                      <span>âœ“</span> PDF (.pdf)
                    </div>
                    <div className="flex items-center gap-2 text-green-400">
                      <span>âœ“</span> Word (.docx, .doc)
                    </div>
                    <div className="flex items-center gap-2 text-green-400">
                      <span>âœ“</span> Excel (.xlsx, .xls)
                    </div>
                    <div className="flex items-center gap-2 text-green-400">
                      <span>âœ“</span> PowerPoint (.pptx, .ppt)
                    </div>
                    <div className="flex items-center gap-2 text-green-400">
                      <span>âœ“</span> Text (.txt)
                    </div>
                    <div className="flex items-center gap-2 text-yellow-400">
                      <span>â³</span> Images (API pending)
                    </div>
                  </div>
                </div>
              </motion.div>
            )}

            {screen === "uploads" && (
              <motion.div
                key="uploads"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                className="space-y-6"
              >
                <div className="flex justify-between items-center">
                  <h1 className="text-3xl font-bold">Document Uploads</h1>
                  <button
                    onClick={fetchDocuments}
                    className="flex items-center gap-2 px-4 py-2 bg-gray-700 hover:bg-gray-600 rounded-lg transition"
                  >
                    <RefreshCw className="w-4 h-4" />
                    Refresh
                  </button>
                </div>

                <div className="border-2 border-dashed border-gray-600 p-8 rounded-lg text-center bg-gray-800/50 hover:border-blue-500 transition">
                  <UploadCloud className="w-16 h-16 mx-auto mb-4 text-gray-400" />
                  <p className="text-gray-300 mb-2">Upload documents to build your knowledge base</p>
                  <p className="text-sm text-gray-500 mb-4">Supports: PDF, Word, Excel, PowerPoint, Text, Images</p>
                  <input
                    type="file"
                    onChange={handleFileUpload}
                    className="hidden"
                    id="file-upload"
                    disabled={uploading}
                    accept=".pdf,.doc,.docx,.xls,.xlsx,.ppt,.pptx,.txt,.jpg,.jpeg,.png,.gif"
                  />
                  <label
                    htmlFor="file-upload"
                    className={`inline-flex items-center gap-2 px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition ${
                      uploading ? "opacity-50 cursor-not-allowed" : "cursor-pointer"
                    }`}
                  >
                    {uploading && <Loader2 className="w-4 h-4 animate-spin" />}
                    {uploading ? "Uploading..." : "Choose File"}
                  </label>
                </div>

                {docs.length === 0 ? (
                  <div className="text-center text-gray-400 py-12 bg-gray-800/30 rounded-lg">
                    <FileText className="w-12 h-12 mx-auto mb-2 opacity-50" />
                    <p>No documents uploaded yet</p>
                  </div>
                ) : (
                  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                    {docs.map((doc) => (
                      <div key={doc.id} className="bg-gray-800 border border-gray-700 p-4 rounded-lg hover:border-blue-500 transition group">
                        <div className="flex justify-between items-start mb-2">
                          <FileText className="w-5 h-5 text-blue-400 flex-shrink-0" />
                          <button
                            onClick={() => handleDeleteDocument(doc.id)}
                            className="text-gray-500 hover:text-red-400 opacity-0 group-hover:opacity-100 transition"
                          >
                            <Trash2 className="w-4 h-4" />
                          </button>
                        </div>
                        <h3 className="font-semibold truncate mb-1 text-gray-200">{doc.name}</h3>
                        <div className="flex items-center justify-between text-xs">
                          <span className="text-gray-400">{doc.size}</span>
                          {doc.type === "image" && (
                            <span className="bg-yellow-900/30 text-yellow-400 px-2 py-1 rounded">Image</span>
                          )}
                        </div>
                        <p className="text-xs text-gray-500 mt-2">{new Date(doc.uploaded_at).toLocaleString()}</p>
                      </div>
                    ))}
                  </div>
                )}
              </motion.div>
            )}

            {screen === "chat" && (
              <motion.div
                key="chat"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                className="space-y-6 max-w-4xl mx-auto"
              >
                <div className="flex justify-between items-center">
                  <h1 className="text-3xl font-bold">Chat with Documents</h1>
                  {bots.length > 0 && (
                    <select
                      value={selectedBot || ""}
                      onChange={(e) => setSelectedBot(e.target.value || null)}
                      className="px-4 py-2 bg-gray-800 border border-gray-700 rounded-lg text-gray-200"
                    >
                      <option value="">Default RAG Agent</option>
                      {bots.map((bot) => (
                        <option key={bot.id} value={bot.id}>{bot.name}</option>
                      ))}
                    </select>
                  )}
                </div>

                <div className="bg-gray-800 border border-gray-700 p-6 rounded-lg shadow-xl min-h-[600px] flex flex-col">
                  <div className="flex-1 overflow-y-auto space-y-4 mb-4">
                    {answer ? (
                      <div className="space-y-4">
                        <div className="p-4 bg-blue-900/30 border border-blue-700 rounded-lg">
                          <p className="font-semibold mb-3 flex items-center gap-2 text-blue-400">
                            <MessageCircle className="w-5 h-5" />
                            Answer:
                          </p>
                          <p className="whitespace-pre-wrap text-gray-200 leading-relaxed">{answer}</p>
                        </div>
                        {sources.length > 0 && (
                          <div className="p-4 bg-gray-700 rounded-lg border border-gray-600">
                            <p className="font-semibold mb-3 text-gray-200">ðŸ“š Sources:</p>
                            <div className="space-y-2">
                              {sources.map((source, idx) => (
                                <div key={idx} className="text-sm p-3 bg-gray-800 rounded border border-gray-600">
                                  <p className="mb-2 text-gray-400 text-xs font-mono">
                                    {source.metadata.source}
                                  </p>
                                  <p className="text-gray-300">{source.content}</p>
                                </div>
                              ))}
                            </div>
                          </div>
                        )}
                      </div>
                    ) : (
                      <div className="flex flex-col items-center justify-center h-full text-gray-400 py-20">
                        <MessageCircle className="w-16 h-16 mb-4 opacity-20" />
                        <p className="text-lg">Ask a question about your documents</p>
                        <p className="text-sm mt-2">Upload some documents first if you haven't</p>
                      </div>
                    )}
                  </div>

                  <div className="flex gap-2 border-t border-gray-700 pt-4">
                    <input
                      type="text"
                      value={question}
                      onChange={(e) => setQuestion(e.target.value)}
                      onKeyPress={(e) => e.key === 'Enter' && !e.shiftKey && handleAsk()}
                      placeholder="Ask a question..."
                      className="flex-1 p-3 bg-gray-900 border border-gray-700 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 text-gray-200 placeholder-gray-500"
                      disabled={asking}
                    />
                    <button
                      onClick={handleAsk}
                      disabled={asking || !question.trim()}
                      className="px-6 py-3 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition disabled:opacity-50 disabled:cursor-not-allowed flex items-center gap-2 font-semibold"
                    >
                      {asking ? (
                        <>
                          <Loader2 className="w-5 h-5 animate-spin" />
                          Thinking...
                        </>
                      ) : (
                        <>
                          <Send className="w-5 h-5" />
                          Ask
                        </>
                      )}
                    </button>
                  </div>
                </div>
              </motion.div>
            )}

            {screen === "bots" && (
              <motion.div
                key="bots"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                className="space-y-6"
              >
                <div className="flex justify-between items-center">
                  <h1 className="text-3xl font-bold">Custom Bots</h1>
                  <button
                    onClick={() => setShowBotForm(!showBotForm)}
                    className="flex items-center gap-2 px-4 py-2 bg-blue-600 text-white rounded-lg hover:bg-blue-700 transition"
                  >
                    <Plus className="w-4 h-4" />
                    Create New Bot
                  </button>
                </div>

                {showBotForm && (
                  <div className="bg-gray-800 border border-gray-700 p-6 rounded-lg shadow space-y-4">
                    <h2 className="text-xl font-semibold">New Bot Configuration</h2>
                    <input
                      type="text"
                      placeholder="Bot Name (e.g., 'Code Assistant', 'Research Helper')"
                      value={newBot.name}
                      onChange={(e) => setNewBot({...newBot, name: e.target.value})}
                      className="w-full p-3 bg-gray-900 border border-gray-700 rounded-lg text-gray-200 placeholder-gray-500"
                    />
                    <textarea
                      placeholder="Bot Instructions (e.g., 'You are a helpful assistant that summarizes documents concisely...')"
                      value={newBot.instructions}
                      onChange={(e) => setNewBot({...newBot, instructions: e.target.value})}
                      className="w-full p-3 bg-gray-900 border border-gray-700 rounded-lg h-32 text-gray-200 placeholder-gray-500"
                    />
                    <div className="grid grid-cols-2 gap-4">
                      <div>
                        <label className="block text-sm mb-2 text-gray-400">Model</label>
                        <select
                          value={newBot.model}
                          onChange={(e) => setNewBot({...newBot, model: e.target.value})}
                          className="w-full p-3 bg-gray-900 border border-gray-700 rounded-lg text-gray-200"
                        >
                          <option value="llama3">Llama 3</option>
                          <option value="mistral">Mistral</option>
                          <option value="phi">Phi</option>
                        </select>
                      </div>
                      <div>
                        <label className="block text-sm mb-2 text-gray-400">Temperature: {newBot.temperature}</label>
                        <input
                          type="range"
                          min="0"
                          max="1"
                          step="0.1"
                          value={newBot.temperature}
                          onChange={(e) => setNewBot({...newBot, temperature: parseFloat(e.target.value)})}
                          className="w-full accent-blue-600"
                        />
                      </div>
                    </div>
                    <div className="flex gap-2">
                      <button
                        onClick={handleCreateBot}
                        className="px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition"
                      >
                        Create Bot
                      </button>
                      <button
                        onClick={() => setShowBotForm(false)}
                        className="px-4 py-2 bg-gray-600 text-white rounded-lg hover:bg-gray-700 transition"
                      >
                        Cancel
                      </button>
                    </div>
                  </div>
                )}

                {bots.length === 0 ? (
                  <div className="text-center text-gray-400 py-12 bg-gray-800/30 rounded-lg">
                    <FileText className="w-12 h-12 mx-auto mb-2 opacity-50" />
                    <p>No custom bots created yet</p>
                    <p className="text-sm mt-2">Create a bot with custom instructions above</p>
                  </div>
                ) : (
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                    {bots.map((bot) => (
                      <div key={bot.id} className="bg-gray-800 border border-gray-700 p-5 rounded-lg hover:border-blue-500 transition group">
                        <div className="flex justify-between items-start mb-3">
                          <h3 className="font-semibold text-lg text-gray-200">{bot.name}</h3>
                          <button
                            onClick={() => handleDeleteBot(bot.id)}
                            className="text-gray-500 hover:text-red-400 opacity-0 group-hover:opacity-100 transition"
                          >
                            <Trash2 className="w-4 h-4" />
                          </button>
                        </div>
                        <p className="text-sm text-gray-400 mb-3 line-clamp-2">{bot.instructions}</p>
                        <div className="flex gap-2 text-xs">
                          <span className="bg-gray-700 text-gray-300 px-2 py-1 rounded">{bot.model}</span>
                          <span className="bg-gray-700 text-gray-300 px-2 py-1 rounded">temp: {bot.temperature}</span>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </motion.div>
            )}

            {screen === "settings" && (
              <motion.div
                key="settings"
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                className="space-y-6 max-w-2xl"
              >
                <h1 className="text-3xl font-bold">Settings</h1>

                <div className="bg-gray-800 border border-gray-700 p-6 rounded-lg shadow space-y-4">
                  <h2 className="text-xl font-semibold">Model Configuration</h2>

                  <div>
                    <label className="block text-sm mb-2 text-gray-400">Default Model</label>
                    <select
                      value={settings.model}
                      onChange={(e) => setSettings({...settings, model: e.target.value})}
                      className="w-full p-3 bg-gray-900 border border-gray-700 rounded-lg text-gray-200"
                    >
                      <option value="llama3">Llama 3 (Recommended)</option>
                      <option value="mistral">Mistral</option>
                      <option value="phi">Phi</option>
                    </select>
                  </div>

                  <div>
                    <label className="block text-sm mb-2 text-gray-400">
                      Temperature: {settings.temperature}
                    </label>
                    <input
                      type="range"
                      min="0"
                      max="1"
                      step="0.1"
                      value={settings.temperature}
                      onChange={(e) => setSettings({...settings, temperature: parseFloat(e.target.value)})}
                      className="w-full accent-blue-600"
                    />
                    <p className="text-xs text-gray-500 mt-1">
                      Lower = more focused, Higher = more creative
                    </p>
                  </div>

                  <div>
                    <label className="block text-sm mb-2 text-gray-400">
                      Max Tokens: {settings.maxTokens}
                    </label>
                    <input
                      type="range"
                      min="500"
                      max="4000"
                      step="100"
                      value={settings.maxTokens}
                      onChange={(e) => setSettings({...settings, maxTokens: parseInt(e.target.value)})}
                      className="w-full accent-blue-600"
                    />
                  </div>
                </div>

                <div className="bg-gray-800 border border-gray-700 p-6 rounded-lg shadow space-y-3">
                  <h2 className="text-xl font-semibold mb-4">System Information</h2>
                  <div className="space-y-2 text-sm">
                    <div className="flex justify-between py-2 border-b border-gray-700">
                      <span className="text-gray-400">Backend Status:</span>
                      <span className={backendStatus === "connected" ? "text-green-400" : "text-red-400"}>
                        {backendStatus === "connected" ? "â— Connected" : "â— Disconnected"}
                      </span>
                    </div>
                    <div className="flex justify-between py-2 border-b border-gray-700">
                      <span className="text-gray-400">Vector Database:</span>
                      <span className="text-gray-200">ChromaDB</span>
                    </div>
                    <div className="flex justify-between py-2 border-b border-gray-700">
                      <span className="text-gray-400">Embedding Model:</span>
                      <span className="text-gray-200">all-MiniLM-L6-v2</span>
                    </div>
                    <div className="flex justify-between py-2">
                      <span className="text-gray-400">Framework:</span>
                      <span className="text-gray-200">LangChain + Ollama</span>
                    </div>
                  </div>
                </div>
              </motion.div>
            )}
          </AnimatePresence>
        </main>
      </div>
    </div>
  );
}
