import { useState, useEffect, useRef } from "react";
import { motion, AnimatePresence } from "framer-motion";
import { 
  UploadCloud, MessageCircle, Settings, FileText, Home, Loader2, 
  Plus, Trash2, Send, AlertCircle, RefreshCw, Check, X, ChevronRight 
} from "lucide-react";

const API_BASE_URL = '/api';

export default function RagBotUI() {
  const [screen, setScreen] = useState("dashboard");
  const [docs, setDocs] = useState([]);
  const [bots, setBots] = useState([]);
  const [chats, setChats] = useState([]);
  const [currentChat, setCurrentChat] = useState(null);
  const [question, setQuestion] = useState("");
  const [uploading, setUploading] = useState(false);
  const [asking, setAsking] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const [error, setError] = useState("");
  const [backendStatus, setBackendStatus] = useState("checking");

  const [showBotForm, setShowBotForm] = useState(false);
  const [newBot, setNewBot] = useState({
    name: "",
    instructions: "",
    model: "llama3",
    temperature: 0.7
  });

  const [showChatConfig, setShowChatConfig] = useState(false);
  const [newChatConfig, setNewChatConfig] = useState({
    title: "",
    selected_documents: [],
    bot_id: null
  });

  const [settings, setSettings] = useState({
    model: "llama3",
    temperature: 0.7,
    maxTokens: 2000,
    topP: 0.9
  });

  const messagesEndRef = useRef(null);
  const eventSourceRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [currentChat?.messages]);

  const checkBackendHealth = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/health`);
      if (response.ok) {
        const data = await response.json();
        setBackendStatus("connected");
        if (!data.ollama_available) {
          setError("âš ï¸ Ollama is not running. Start it with: ollama serve");
        }
        return true;
      } else {
        setBackendStatus("disconnected");
        return false;
      }
    } catch (err) {
      setBackendStatus("disconnected");
      return false;
    }
  };

  useEffect(() => {
    const init = async () => {
      const healthy = await checkBackendHealth();
      if (healthy) {
        await fetchDocuments();
        await fetchBots();
        await fetchChats();
      }
    };
    init();
    const interval = setInterval(checkBackendHealth, 30000);
    return () => clearInterval(interval);
  }, []);

  const fetchDocuments = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/documents`);
      if (response.ok) {
        const data = await response.json();
        setDocs(data.documents || []);
        setError("");
      }
    } catch (err) {
      console.error("Failed to fetch documents:", err);
    }
  };

  const fetchBots = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/bots`);
      if (response.ok) {
        const data = await response.json();
        setBots(data.bots || []);
      }
    } catch (err) {
      console.error("Failed to fetch bots:", err);
    }
  };

  const fetchChats = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/chats`);
      if (response.ok) {
        const data = await response.json();
        setChats(data.chats || []);
      }
    } catch (err) {
      console.error("Failed to fetch chats:", err);
    }
  };

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    setUploading(true);
    setError("");

    const formData = new FormData();
    formData.append("file", file);

    try {
      const response = await fetch(`${API_BASE_URL}/upload`, {
        method: "POST",
        body: formData,
      });

      const result = await response.json();

      if (!response.ok) {
        throw new Error(result.error || 'Upload failed');
      }

      await fetchDocuments();
      e.target.value = "";

      const message = result.type === "image" 
        ? `âœ… ${result.message}\nðŸ“Š Chunks: ${result.chunks}\nðŸ“ Extracted: ${result.extracted_length} chars`
        : `âœ… ${result.message}\nðŸ“Š Chunks processed: ${result.chunks}`;

      alert(message);
    } catch (err) {
      setError(`Upload failed: ${err.message}`);
    } finally {
      setUploading(false);
    }
  };

  const handleDeleteDocument = async (docId) => {
    if (!window.confirm("Are you sure you want to delete this document?")) return;

    try {
      const response = await fetch(`${API_BASE_URL}/documents/${docId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        await fetchDocuments();
        alert("âœ… Document deleted successfully");
      } else {
        throw new Error('Failed to delete document');
      }
    } catch (err) {
      alert(`Failed to delete document: ${err.message}`);
    }
  };

  const handleCreateChat = async () => {
    try {
      const response = await fetch(`${API_BASE_URL}/chats/create`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          title: newChatConfig.title || "New Chat",
          selected_documents: newChatConfig.selected_documents,
          bot_id: newChatConfig.bot_id
        }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to create chat');
      }

      await fetchChats();
      setCurrentChat(data.chat);
      setShowChatConfig(false);
      setNewChatConfig({ title: "", selected_documents: [], bot_id: null });
      setScreen("chat");
    } catch (err) {
      alert(`Failed to create chat: ${err.message}`);
    }
  };

  const handleDeleteChat = async (chatId) => {
    if (!window.confirm("Delete this chat?")) return;

    try {
      const response = await fetch(`${API_BASE_URL}/chats/${chatId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        await fetchChats();
        if (currentChat?.id === chatId) {
          setCurrentChat(null);
        }
      }
    } catch (err) {
      alert(`Failed to delete chat: ${err.message}`);
    }
  };

  const handleSelectChat = async (chatId) => {
    // Prevent loading same chat twice
    if (currentChat?.id === chatId) {
      setScreen("chat");
      return;
    }

    try {
      const response = await fetch(`${API_BASE_URL}/chats/${chatId}`);
      if (response.ok) {
        const data = await response.json();
        setCurrentChat(data.chat);
        setScreen("chat");
      }
    } catch (err) {
      console.error("Failed to load chat:", err);
    }
  };

  const handleAskStreaming = async () => {
    if (!question.trim() || !currentChat) return;

    setAsking(true);
    setError("");

    const userMessage = {
      id: Date.now().toString(),
      role: "user",
      content: question,
      timestamp: new Date().toISOString()
    };

    const assistantMessage = {
      id: (Date.now() + 1).toString(),
      role: "assistant",
      content: "",
      sources: [],
      timestamp: new Date().toISOString(),
      streaming: true
    };

    setCurrentChat(prev => ({
      ...prev,
      messages: [...prev.messages, userMessage, assistantMessage]
    }));

    const currentQuestion = question;
    setQuestion("");

    try {
      const response = await fetch(`${API_BASE_URL}/chats/${currentChat.id}/ask`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ question: currentQuestion }),
      });

      if (!response.ok) {
        throw new Error('Failed to get response');
      }

      const reader = response.body.getReader();
      const decoder = new TextDecoder();
      let buffer = '';

      while (true) {
        const { done, value } = await reader.read();
        if (done) break;

        buffer += decoder.decode(value, { stream: true });

        const lines = buffer.split('\n');
        buffer = lines.pop() || '';

        for (const line of lines) {
          if (!line.trim() || !line.startsWith('data: ')) continue;

          try {
            const data = JSON.parse(line.slice(6));

            if (data.token) {
              setCurrentChat(prev => {
                const messages = [...prev.messages];
                const lastMsg = messages[messages.length - 1];
                if (lastMsg && lastMsg.role === 'assistant' && lastMsg.streaming) {
                  lastMsg.content += data.token;
                }
                return { ...prev, messages };
              });
            }

            if (data.sources) {
              setCurrentChat(prev => {
                const messages = [...prev.messages];
                const lastMsg = messages[messages.length - 1];
                if (lastMsg && lastMsg.role === 'assistant') {
                  lastMsg.sources = data.sources;
                }
                return { ...prev, messages };
              });
            }

            if (data.done) {
              setCurrentChat(prev => {
                const messages = [...prev.messages];
                const lastMsg = messages[messages.length - 1];
                if (lastMsg && lastMsg.role === 'assistant') {
                  delete lastMsg.streaming;
                }
                return { ...prev, messages };
              });
            }

            if (data.error) {
              throw new Error(data.error);
            }
          } catch (parseError) {
            if (line.trim()) {
              console.warn('Skipped invalid JSON:', line.slice(0, 50));
            }
          }
        }
      }

      await fetchChats();

    } catch (err) {
      setError(`Failed to get answer: ${err.message}`);
      setCurrentChat(prev => {
        const messages = prev.messages.slice(0, -1);
        return { ...prev, messages };
      });
    } finally {
      setAsking(false);
    }
  };

  const handleCreateBot = async () => {
    if (!newBot.name || !newBot.instructions) {
      alert("Please fill in bot name and instructions");
      return;
    }

    try {
      const response = await fetch(`${API_BASE_URL}/bots/create`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(newBot),
      });

      if (response.ok) {
        await fetchBots();
        setShowBotForm(false);
        setNewBot({ name: "", instructions: "", model: "llama3", temperature: 0.7 });
        alert("âœ… Bot created successfully");
      } else {
        const data = await response.json();
        throw new Error(data.error || 'Failed to create bot');
      }
    } catch (err) {
      alert(`Failed to create bot: ${err.message}`);
    }
  };

  const handleDeleteBot = async (botId) => {
    if (!window.confirm("Delete this bot?")) return;

    try {
      const response = await fetch(`${API_BASE_URL}/bots/${botId}`, {
        method: 'DELETE',
      });

      if (response.ok) {
        await fetchBots();
        alert("âœ… Bot deleted successfully");
      }
    } catch (err) {
      alert(`Failed to delete bot: ${err.message}`);
    }
  };

  // UI Components
  const Sidebar = () => (
    <motion.div
      initial={{ x: -300 }}
      animate={{ x: isSidebarOpen ? 0 : -300 }}
      className="w-72 bg-gradient-to-b from-gray-900 to-gray-800 border-r border-gray-700 flex flex-col"
    >
      <div className="p-6 border-b border-gray-700">
        <h1 className="text-2xl font-bold bg-gradient-to-r from-blue-400 to-purple-500 bg-clip-text text-transparent">
          HKU RAG Bot
        </h1>
        <p className="text-gray-400 text-sm mt-1">
          {backendStatus === "connected" ? "ðŸŸ¢ Connected" : "ðŸ”´ Disconnected"}
        </p>
      </div>

      <nav className="flex-1 p-4 space-y-2 overflow-y-auto">
        <NavButton icon={Home} label="Dashboard" onClick={() => setScreen("dashboard")} active={screen === "dashboard"} />
        <NavButton icon={UploadCloud} label="Upload" onClick={() => setScreen("upload")} active={screen === "upload"} />
        <NavButton icon={MessageCircle} label="Chats" onClick={() => setScreen("chatlist")} active={screen === "chatlist"} />
        <NavButton icon={Settings} label="Bots" onClick={() => setScreen("bots")} active={screen === "bots"} />
      </nav>
    </motion.div>
  );

  const NavButton = ({ icon: Icon, label, onClick, active }) => (
    <button
      onClick={onClick}
      className={`w-full flex items-center gap-3 px-4 py-3 rounded-lg transition ${
        active
          ? "bg-blue-600 text-white"
          : "text-gray-300 hover:bg-gray-700"
      }`}
    >
      <Icon className="w-5 h-5" />
      <span>{label}</span>
    </button>
  );

  const DashboardScreen = () => (
    <div className="p-8 space-y-6">
      <h2 className="text-3xl font-bold text-white">Dashboard</h2>

      {error && (
        <div className="bg-red-500/10 border border-red-500 rounded-lg p-4 flex items-start gap-3">
          <AlertCircle className="w-5 h-5 text-red-500 mt-0.5" />
          <div className="text-red-400">{error}</div>
        </div>
      )}

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        <StatCard
          icon={FileText}
          label="Documents"
          value={docs.length}
          color="blue"
        />
        <StatCard
          icon={MessageCircle}
          label="Chats"
          value={chats.length}
          color="purple"
        />
        <StatCard
          icon={Settings}
          label="Bots"
          value={bots.length}
          color="green"
        />
      </div>

      <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
        <h3 className="text-xl font-semibold text-white mb-4">Recent Activity</h3>
        <div className="space-y-3">
          {chats.slice(0, 5).map(chat => (
            <div key={chat.id} className="flex items-center justify-between p-3 bg-gray-700/50 rounded-lg">
              <div className="flex items-center gap-3">
                <MessageCircle className="w-5 h-5 text-blue-400" />
                <div>
                  <p className="text-white font-medium">{chat.title}</p>
                  <p className="text-gray-400 text-sm">{new Date(chat.updated_at).toLocaleString()}</p>
                </div>
              </div>
              <button
                onClick={() => handleSelectChat(chat.id)}
                className="px-4 py-2 bg-blue-600 hover:bg-blue-700 rounded-lg text-white transition"
              >
                Open
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  const StatCard = ({ icon: Icon, label, value, color }) => {
    const colors = {
      blue: "from-blue-500 to-blue-600",
      purple: "from-purple-500 to-purple-600",
      green: "from-green-500 to-green-600"
    };

    return (
      <div className={`bg-gradient-to-br ${colors[color]} rounded-lg p-6 text-white`}>
        <div className="flex items-center justify-between">
          <div>
            <p className="text-sm opacity-90">{label}</p>
            <p className="text-4xl font-bold mt-2">{value}</p>
          </div>
          <Icon className="w-12 h-12 opacity-50" />
        </div>
      </div>
    );
  };

  const UploadScreen = () => (
    <div className="p-8 space-y-6">
      <div className="flex items-center justify-between">
        <h2 className="text-3xl font-bold text-white">Upload Documents</h2>
        <button
          onClick={fetchDocuments}
          className="flex items-center gap-2 px-4 py-2 bg-gray-700 hover:bg-gray-600 rounded-lg transition"
        >
          <RefreshCw className="w-4 h-4" />
          Refresh
        </button>
      </div>

      <div className="bg-gray-800 rounded-lg p-8 border-2 border-dashed border-gray-600">
        <label className="flex flex-col items-center justify-center cursor-pointer">
          <UploadCloud className="w-16 h-16 text-gray-400 mb-4" />
          <p className="text-white mb-2">Click to upload or drag and drop</p>
          <p className="text-gray-400 text-sm mb-4">PDF, DOCX, XLSX, PPTX, TXT, Images</p>
          <input
            type="file"
            onChange={handleFileUpload}
            className="hidden"
            accept=".pdf,.docx,.doc,.xlsx,.xls,.pptx,.ppt,.txt,.jpg,.jpeg,.png,.gif,.bmp,.tiff,.webp"
          />
          {uploading && (
            <div className="flex items-center gap-2 text-blue-400">
              <Loader2 className="w-5 h-5 animate-spin" />
              <span>Uploading...</span>
            </div>
          )}
        </label>
      </div>

      <div className="bg-gray-800 rounded-lg p-6 border border-gray-700">
        <h3 className="text-xl font-semibold text-white mb-4">Uploaded Documents ({docs.length})</h3>
        <div className="space-y-3">
          {docs.map(doc => (
            <div key={doc.id} className="flex items-center justify-between p-4 bg-gray-700/50 rounded-lg">
              <div className="flex items-center gap-3">
                <FileText className="w-5 h-5 text-blue-400" />
                <div>
                  <p className="text-white font-medium">{doc.name}</p>
                  <p className="text-gray-400 text-sm">{doc.size} â€¢ {doc.type}</p>
                </div>
              </div>
              <button
                onClick={() => handleDeleteDocument(doc.id)}
                className="p-2 text-red-400 hover:bg-red-500/10 rounded-lg transition"
              >
                <Trash2 className="w-5 h-5" />
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  );

  const ChatListScreen = () => (
    <div className="p-8 space-y-6">
      <div className="flex items-center justify-between">
        <h2 className="text-3xl font-bold text-white">Chats</h2>
        <button
          onClick={() => setShowChatConfig(true)}
          className="flex items-center gap-2 px-4 py-2 bg-blue-600 hover:bg-blue-700 rounded-lg transition"
        >
          <Plus className="w-4 h-4" />
          New Chat
        </button>
      </div>

      {showChatConfig && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
          <div className="bg-gray-800 rounded-lg p-6 w-full max-w-md border border-gray-700">
            <h3 className="text-xl font-semibold text-white mb-4">Create New Chat</h3>
            <div className="space-y-4">
              <input
                type="text"
                placeholder="Chat title (optional)"
                value={newChatConfig.title}
                onChange={(e) => setNewChatConfig({...newChatConfig, title: e.target.value})}
                className="w-full px-4 py-2 bg-gray-700 border border-gray-600 rounded-lg text-white"
              />

              <div>
                <label className="text-gray-300 text-sm mb-2 block">Select Documents (optional)</label>
                <div className="max-h-40 overflow-y-auto space-y-2">
                  {docs.map(doc => (
                    <label key={doc.id} className="flex items-center gap-2 text-white">
                      <input
                        type="checkbox"
                        checked={newChatConfig.selected_documents.includes(doc.id)}
                        onChange={(e) => {
                          if (e.target.checked) {
                            setNewChatConfig({
                              ...newChatConfig,
                              selected_documents: [...newChatConfig.selected_documents, doc.id]
                            });
                          } else {
                            setNewChatConfig({
                              ...newChatConfig,
                              selected_documents: newChatConfig.selected_documents.filter(id => id !== doc.id)
                            });
                          }
                        }}
                      />
                      <span className="text-sm">{doc.name}</span>
                    </label>
                  ))}
                </div>
              </div>

              <div>
                <label className="text-gray-300 text-sm mb-2 block">Select Bot (optional)</label>
                <select
                  value={newChatConfig.bot_id || ""}
                  onChange={(e) => setNewChatConfig({...newChatConfig, bot_id: e.target.value || null})}
                  className="w-full px-4 py-2 bg-gray-700 border border-gray-600 rounded-lg text-white"
                >
                  <option value="">No Bot (Default)</option>
                  {bots.map(bot => (
                    <option key={bot.id} value={bot.id}>{bot.name}</option>
                  ))}
                </select>
              </div>

              <div className="flex gap-3">
                <button
                  onClick={handleCreateChat}
                  className="flex-1 px-4 py-2 bg-blue-600 hover:bg-blue-700 rounded-lg transition"
                >
                  Create
                </button>
                <button
                  onClick={() => {
                    setShowChatConfig(false);
                    setNewChatConfig({ title: "", selected_documents: [], bot_id: null });
                  }}
                  className="flex-1 px-4 py-2 bg-gray-700 hover:bg-gray-600 rounded-lg transition"
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      <div className="space-y-3">
        {chats.map(chat => (
          <div key={chat.id} className="flex items-center justify-between p-4 bg-gray-800 rounded-lg border border-gray-700">
            <div className="flex-1 cursor-pointer" onClick={() => handleSelectChat(chat.id)}>
              <p className="text-white font-medium">{chat.title}</p>
              <p className="text-gray-400 text-sm">{chat.messages.length} messages â€¢ {new Date(chat.updated_at).toLocaleString()}</p>
            </div>
            <button
              onClick={() => handleDeleteChat(chat.id)}
              className="p-2 text-red-400 hover:bg-red-500/10 rounded-lg transition"
            >
              <Trash2 className="w-5 h-5" />
            </button>
          </div>
        ))}
      </div>
    </div>
  );

  const ChatScreen = () => (
    <div className="flex flex-col h-full">
      <div className="p-6 border-b border-gray-700">
        <h2 className="text-2xl font-bold text-white">{currentChat?.title || "Chat"}</h2>
        <p className="text-gray-400 text-sm">{currentChat?.messages.length || 0} messages</p>
      </div>

      <div className="flex-1 overflow-y-auto p-6 space-y-4">
        {currentChat?.messages.map(msg => (
          <div key={msg.id} className={`flex ${msg.role === 'user' ? 'justify-end' : 'justify-start'}`}>
            <div className={`max-w-2xl px-4 py-3 rounded-lg ${
              msg.role === 'user' ? 'bg-blue-600 text-white' : 'bg-gray-800 text-white'
            }`}>
              <p className="whitespace-pre-wrap">{msg.content}</p>
              {msg.streaming && (
                <span className="inline-block w-2 h-4 bg-white animate-pulse ml-1" />
              )}
              {msg.sources && msg.sources.length > 0 && (
                <div className="mt-3 pt-3 border-t border-gray-700">
                  <p className="text-xs text-gray-400 mb-2">Sources:</p>
                  {msg.sources.map((src, i) => (
                    <div key={i} className="text-xs text-gray-400 mt-1">
                      ðŸ“„ {src.metadata.source}
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>
        ))}
        <div ref={messagesEndRef} />
      </div>

      <div className="p-6 border-t border-gray-700">
        <div className="flex gap-3">
          <input
            type="text"
            value={question}
            onChange={(e) => setQuestion(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && !asking && handleAskStreaming()}
            placeholder="Ask a question..."
            disabled={asking}
            className="flex-1 px-4 py-3 bg-gray-800 border border-gray-700 rounded-lg text-white focus:outline-none focus:border-blue-500"
          />
          <button
            onClick={handleAskStreaming}
            disabled={asking || !question.trim()}
            className="px-6 py-3 bg-blue-600 hover:bg-blue-700 disabled:bg-gray-700 rounded-lg transition flex items-center gap-2"
          >
            {asking ? <Loader2 className="w-5 h-5 animate-spin" /> : <Send className="w-5 h-5" />}
          </button>
        </div>
      </div>
    </div>
  );

  const BotsScreen = () => (
    <div className="p-8 space-y-6">
      <div className="flex items-center justify-between">
        <h2 className="text-3xl font-bold text-white">Bots</h2>
        <button
          onClick={() => setShowBotForm(true)}
          className="flex items-center gap-2 px-4 py-2 bg-blue-600 hover:bg-blue-700 rounded-lg transition"
        >
          <Plus className="w-4 h-4" />
          New Bot
        </button>
      </div>

      {showBotForm && (
        <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
          <div className="bg-gray-800 rounded-lg p-6 w-full max-w-md border border-gray-700">
            <h3 className="text-xl font-semibold text-white mb-4">Create New Bot</h3>
            <div className="space-y-4">
              <input
                type="text"
                placeholder="Bot name"
                value={newBot.name}
                onChange={(e) => setNewBot({...newBot, name: e.target.value})}
                className="w-full px-4 py-2 bg-gray-700 border border-gray-600 rounded-lg text-white"
              />
              <textarea
                placeholder="System instructions"
                value={newBot.instructions}
                onChange={(e) => setNewBot({...newBot, instructions: e.target.value})}
                rows={4}
                className="w-full px-4 py-2 bg-gray-700 border border-gray-600 rounded-lg text-white"
              />
              <select
                value={newBot.model}
                onChange={(e) => setNewBot({...newBot, model: e.target.value})}
                className="w-full px-4 py-2 bg-gray-700 border border-gray-600 rounded-lg text-white"
              >
                <option value="llama3">Llama 3</option>
                <option value="llama2">Llama 2</option>
                <option value="mistral">Mistral</option>
              </select>
              <div>
                <label className="text-gray-300 text-sm">Temperature: {newBot.temperature}</label>
                <input
                  type="range"
                  min="0"
                  max="1"
                  step="0.1"
                  value={newBot.temperature}
                  onChange={(e) => setNewBot({...newBot, temperature: parseFloat(e.target.value)})}
                  className="w-full"
                />
              </div>
              <div className="flex gap-3">
                <button
                  onClick={handleCreateBot}
                  className="flex-1 px-4 py-2 bg-blue-600 hover:bg-blue-700 rounded-lg transition"
                >
                  Create
                </button>
                <button
                  onClick={() => {
                    setShowBotForm(false);
                    setNewBot({ name: "", instructions: "", model: "llama3", temperature: 0.7 });
                  }}
                  className="flex-1 px-4 py-2 bg-gray-700 hover:bg-gray-600 rounded-lg transition"
                >
                  Cancel
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      <div className="space-y-3">
        {bots.map(bot => (
          <div key={bot.id} className="p-4 bg-gray-800 rounded-lg border border-gray-700">
            <div className="flex items-start justify-between">
              <div className="flex-1">
                <h3 className="text-white font-semibold">{bot.name}</h3>
                <p className="text-gray-400 text-sm mt-1">{bot.instructions}</p>
                <p className="text-gray-500 text-xs mt-2">Model: {bot.model} â€¢ Temp: {bot.temperature}</p>
              </div>
              <button
                onClick={() => handleDeleteBot(bot.id)}
                className="p-2 text-red-400 hover:bg-red-500/10 rounded-lg transition"
              >
                <Trash2 className="w-5 h-5" />
              </button>
            </div>
          </div>
        ))}
      </div>
    </div>
  );

  return (
    <div className="flex h-screen bg-gray-900 text-white">
      <Sidebar />
      <main className="flex-1 overflow-auto">
        {screen === "dashboard" && <DashboardScreen />}
        {screen === "upload" && <UploadScreen />}
        {screen === "chatlist" && <ChatListScreen />}
        {screen === "chat" && currentChat && <ChatScreen />}
        {screen === "bots" && <BotsScreen />}
      </main>
    </div>
  );
}
